const getLocationFromZip = require("./ziptoloc");
const getWeather = require("./getWeather");

module.exports = options => {
  return new Promise((resolve, response) => {
    getLocationFromZip(options.zip, options.ZipCodeApiKey)
    .then(result => getWeather(result.lat, result.lng, options.DarkSkyKey))
    .then(result => resolve(result))
    .catch(reason => {
      console.error(reason);
    });
  });
}
